import React,{Component} from 'react';

class LoginForm extends Component{


    constructor(props){
        super(props)
        this.state={email:"", password:""}
    }

    emailHandler = (event)=>{
        console.log(`${event.target.value}`)
        this.setState({email:event.target.value})
    }
    passwordHandler = (event)=>{
        console.log(`${event.target.value}`)
        this.setState({password:event.target.value})
    }
    submitHandler = ()=>{
        this.props.parentcallback(this.state)
    }


    render(props){
        return(
            <div>
                <form onSubmit={this.submitHandler}>
                        <div class="row row-space">
                            <div class="col-4">
                                <div class="input-group">
                                    <label class="label">research title </label>
                                    <input name="email" type="text" value={this.state.email} onChange={this.emailHandler}/>
                                </div>
                            </div>
                        </div>
                        <div class="row row-space">
                            <div class="col-4">
                                <div class="input-group">
                                    <label class="label">description</label>
                                    <input name="Password" type="password" value={this.state.password} onChange={this.passwordHandler}/>
                                </div>
                            </div>
                        </div>
                    <input name="email" type="text" value={this.state.email} onChange={this.emailHandler}></input>
                    <input name="Password" type="password" value={this.state.password} onChange={this.passwordHandler}></input>
                    <input type="submit"/>
                </form>
            </div>
        )
    }
}
export default LoginForm;
