import React,{Component} from 'react';
import LoginForm from './LoginForm'
import RegisterForm from './RegisterForm';

class Login extends Component{

    constructor(){
        super()
        this.registerRef=React.createRef();
        this.state={isRegisterClicked:false}
    }
    render(){
        let shouldRegisterFormVisible
        this.shouldRegisterFormVisible = this.state.isRegisterClicked?<RegisterForm/>:''
        return(
            <div>
                <LoginForm/>
                {this.shouldRegisterFormVisible}
                <button ref={this.registerRef} onClick={this.registerHandler}>Register</button>
            </div>
        )
    }
    registerHandler=()=>{
        this.setState({
            isRegisterClicked:true
        })
    }
}
export default Login
