import React,{Component} from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import LoginForm from './LoginForm';
import './LoginForm.css'

class RegisterForm extends Component{


    constructor(){
        super()
        this.state={show:false}
    }
    render(){
        return (
            <div class="wrapper wrapper--w360">
                <Card bg='light'>
                    <Card.Header>
                        Log In
                    </Card.Header>
                    <Card.Body>
                        <form onSubmit={this.submitHandler}>
                                <div >
                                    <div >
                                        <div class="input-group">
                                            <label class="label">Email ID</label>
                                            <input class="input--style-4" name="email" type="text" value={this.state.email} onChange={this.emailHandler}/>
                                        </div>
                                    </div>
                                </div>
                                <div >
                                    <div >
                                        <div class="input-group">
                                            <label class="label">Password</label>
                                            <input class="input--style-4" name="Password" type="password" value={this.state.password} onChange={this.passwordHandler}/>
                                        </div>
                                    </div>
                                </div>
                                <div >
                                    <Button variant='primary' type="submit">Submit</Button>
                                </div>
                        </form>
                    </Card.Body>
                </Card>
        </div>
            )
    }

    closeModal=()=>{
        this.setState({show:false})
    }

    openModal=()=>{
        this.setState({show:true})
    }
}
export default RegisterForm
