  const [notificatinBar, setNotificationBar] = useState(false);

  const showNotificationBar = () => setNotificationBar(!notificatinBar);
    return (
        <nav className={true ? 'notification-menu active' : 'notification-menu'}>
          <h2 class='notification-menu-header notificationheading'>Notifications</h2>
          {NotificationData.map((item, index) => {
              return (
                <Card style='notification-card '>
                  {item.text}
                </Card>

              )
            })}
        </nav>
    )
}

export default Notification;
