import React,{Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import './Form.css'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import {LoginDetails} from '../Utils/LoginDetails';
//import Dashboard from './Dashboard';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
class LoginForm extends Component{


    constructor(props){
        super(props)
        this.state={value:''}
    }

    emailHandler = (event,field)=>{
        console.log(`${event.target.value}`)
        this.setState({value:event.target.value})
        field.value=event.value
    }
    passwordHandler = (event)=>{
        console.log(`${event.target.value}`)
        this.setState({password:event.target.value})
    }
    submitHandler = ()=>{
        this.props.parentcallback(this.state)
    }


    render(props){
        return(
            <div class="wrapper wrapper--w360">
                <Card >
                    <Card.Header style={{background:'#c2caf7'}}>
                        Log In
                    </Card.Header>
                    <Card.Body style={{background:'#d8dbf0'}}>
                        <Form>
                            {LoginDetails.map((field)=>(
                                <Form.Group>
                                    <Form.Label>{field.name}</Form.Label>
                                    <Form.Control onChange={this.emailHandler(field)} type={field.type} value={field.value}></Form.Control>
                                </Form.Group>
                            ))}
                        </Form>
                    </Card.Body>
                </Card>
        </div>
        )
    }
}
export default LoginForm;
