import React, { Component} from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import SearchBar from './SearchBar';
import FilterDropDowns from './FilterDropDowns';
import {options} from '../Utils/DropDownOprions';
import ListGroup from 'react-bootstrap/ListGroup';
import UseFiltersToggle from './UseFiltersToggle';

class FilterNavBar extends React.Component {


  constructor(props) {
    super(props);
    this.dropDownOptions=options;
  }

    render() {

      this.filterDropDownsComponent=this.dropDownOptions.map((details)=>(
        <ListGroup  className="my-2" key={details.id}>
            <ListGroup.Item><FilterDropDowns details={details}/></ListGroup.Item>
            </ListGroup>
      ))

        return(

            <Navbar bg="light" expand="lg">
  <Navbar.Brand href="#home">Filter Search</Navbar.Brand>
  <Navbar.Toggle aria-controls="basic-navbar-nav" />
  <Navbar.Collapse id="basic-navbar-nav">
    <Nav className="mr-auto">
      {this.filterDropDownsComponent}
      <UseFiltersToggle/>
    </Nav>
    <SearchBar/>

  </Navbar.Collapse>
</Navbar>

        )
    }
}
export default FilterNavBar;
