import React, { Component } from 'react'
import Card from 'react-bootstrap/Card'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import ButtonToolbar from 'react-bootstrap/ButtonToolbar'
import Button from 'react-bootstrap/Button'
import Image from 'react-bootstrap/Image'
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import rImg from '../../Utils/Images/Rohith.JPG';
import AbstractModal from './AbstractModal';
import { Link } from 'react-router-dom';
import * as IoIcons from 'react-icons/io5'
import * as AiIcons from 'react-icons/ai';


//import './LoginForm.css'
export class ResearchCard extends Component {

    constructor(props) {
        super(props);
        this.modalRef=React.createRef();
        this.state={
            isLiked:false,
            isSaved:false
        }
    }

    openAbstractModal=()=>{
        this.modalRef.current.openAbstractModal();
    }
    handleLike=()=>{
        this.setState({isLiked:!this.state.isLiked});
    }
    handleSave=()=>{
        this.setState({isSaved:!this.state.isSaved});
    }
    render() {
        return (
            <>
            <div style={{padding:'20px'}} class="wrapper wrapper--w480">
               <Card >
                    <Card.Header style={{height:'70px',background:'#aeb9f7'}}>
                        <Container>
                            <Row>
                                <Col md={2}>
                                    <Image  width="40" height="35" src={rImg} roundedCircle />   
                                </Col>
                                <Col md={9}>
                                    <Row>
                                        <Link to='/profile'><h5>{this.props.details.author}</h5></Link>
                                    </Row>
                                    <Row>
                                        <h7>27 April at 17:44  </h7>
                                    </Row>
                                </Col>
                                <Col md={1}>
                                <Link to='/view'>
                                    <IoIcons.IoEyeSharp size={40}/>
                                </Link>
                                </Col>
                            </Row>
                        </Container>
                    </Card.Header>
                    <Card.Body style={{background:'#d8dbf0'}}>
                        <Card.Title>{this.props.details.title}</Card.Title>
                        <Card.Subtitle>{this.props.details.status}</Card.Subtitle>
                        <hr/>
                        <Card.Text>
                            {this.props.details.abstract}
                            <Card.Link onClick={this.openAbstractModal}>Read More</Card.Link>
                        </Card.Text>

                        <div>

                            <ButtonToolbar className="justify-content-between">
                            <ButtonGroup className='mr-2'>
                                <Button variant={!this.state.isLiked?'secondary':'primary'} onClick={this.handleLike}>{!this.state.isLiked?<AiIcons.AiFillLike/>:<AiIcons.AiFillDislike/>}</Button>
                            </ButtonGroup>
                            <ButtonGroup className='mr-2'>
                                <Button onClick={this.handleSave} variant={!this.state.isSaved?'secondary':'primary'}>{!this.state.isSaved?'Save':'UnSave'}</Button>
                            </ButtonGroup>
                            </ButtonToolbar>
                        </div>
                        </Card.Body> 
                    </Card>
            </div>
            <AbstractModal title={this.props.details.title} abstract={this.props.details.abstract} ref={this.modalRef}/>
            </>
        )
    }
}

export default ResearchCard
