import React, { Component, useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import './Form.css'
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import rImg from "../Utils/Images/Rohith.JPG";
import { FaRegImage } from 'react-icons/fa';
import DisplayFeilds from './DisplayFields'
import { UserDetails } from "../Utils/UserDetails"


const formRef = React.createRef();
const handleEdit =()=>{
    console.log(formRef.current);
    formRef.current.changeFormState();
}
const UserProfile = () => {

    const [details, setDetails] = useState(UserDetails)
    //setDetails(UserDetails)
    const uname=details.filter(field=>field.name=='Username')[0].value
    const desc=details.filter(field=>field.name=='Description')[0].value
    return (
        <div style={{ position: 'relative', top: '150px' }}>
        <Container>
            <Row>
                <Col xs={12} md="auto" width=''>
                    <Card style={{ width: '20rem' }}>
                        <Card>
                            <Image src={rImg} width="300" height="300" roundedCircle />
                        </Card>
                        <Card.Body>
                            <Card.Title>
                                {uname}
                            </Card.Title>
                            <Card.Text>
                                {desc}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
                <Col xs={6} md={5}>
                    <Form>
                        <DisplayFeilds ref={formRef} details={details} />
                        <Button onClick={handleEdit} variant="primary">Edit</Button>
                    </Form>
                </Col>
            </Row>
        </Container>
        </div>

    )
}

export default UserProfile
