import React, {Component} from 'react'
import Hello from './Greet'
class Welcome extends Component {

    constructor(){
        super()
        this.state={
            message:"Welcome visitor",
            flag:false
        }
    }

    render(){
        console.log(this.props.children)
        return(
             <div>
         {this.state.message}
         <button hidden={this.state.flag} onClick={()=> this.change()}>Subscribe</button>
         </div>
        )
    }
    change() {
        if(this.state.message == "Welcome visitor"){
            this.setState({message:"thanks for subscribing"})
        }
        else{
            this.setState({message:<Hello/>,flag:true});
        }
    }


}
export default Welcome
