import React,{Component} from 'react';
import LoginForm from './LoginForm'

class Login extends Component{

    constructor(){
        super()
        this.registerRef=React.createRef();
    }
    render(){
        return(
            <div>
                <LoginForm/>
                <button ref={this.registerRef} onClick={this.registerHandler}>Register</button>
            </div>
        )
    }
    registerHandler=()=>{
        console.log(`${this.registerRef.current}`)
    }
}
export default Login
